import logo from './logo.svg';
import './App.css';
import Header from './components/Header';
import ProductList from './components/ProductList';
import { useState } from 'react';
import CartList from './components/CartList';
import UplaodAndDisplayImage from './components/UploadAndDisplayImage';

function App() {

  const [cart, setCart] = useState([])
  const [showCart, setShowCart] = useState(false)
  const [showModal, setShowModal] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);
  const [imageUrl, setImageUrl] = useState('');


  const handleClick = () => {
    setShowModal(true);
  }

  <button onclick={handleClick}>Add an Item</button>

  const [product, setProducts] = useState([
    {
      url: 'https://static.nike.com/a/images/t_default/80b8277d-ede8-4885-9c37-2fe1cdf341aa/air-max-90-shoes-K0mczj.png',
      name: 'Nike Air Max 90',
      category: 'Shoes',
      seller: 'Nike Official Store',
      price: 3999
    },
    {
      url: 'https://www.capital.com.ph/cdn/shop/products/GX9140-140_grande.jpg?v=1664769628',
      name: 'Adidas Ultraboost',
      category: 'Shoes',
      seller: 'Adidas Online',
      price: 2999
    },
    {
      url: 'https://www.capital.com.ph/cdn/shop/products/373308-01.jpg?v=1663221741',
      name: 'Puma RS-X3',
      category: 'Shoes',
      seller: 'Puma Store',
      price: 7999
    },
    {
      url: 'https://i.ebayimg.com/images/g/JmMAAOSw~otWdL5u/s-l1200.jpg',
      name: 'Vans Old Skool',
      category: 'Shoes',
      seller: 'Vans Official',
      price: 1499
    },


  ])

  const addToCart = (data) => {
    setCart([...cart, { ...data, quantity: 1 }])
  }

  const handleShow = (value) => {
    setShowCart(value)
  }

  return (
    <div className="App">
      <Header count={cart.length} handleShow={handleShow} />

      <button onClick={handleClick}>Add an Item</button>

      {/* Conditionally render modal */}
      {showModal && (
        <div className="modal">
          <div className="modal-content"> {/* Black background */}
            {
              
              <div>
              <h1>Add Item</h1>
        
              {selectedImage && (
                <div>
                  <img
                    alt="not found"
                    width={"250px"}
                    src={URL.createObjectURL(selectedImage)}
                  />
                  <br />
                  <button onClick={() => setSelectedImage(null)}>Remove</button>
                </div>
              )}
        
              <br />
              <br />
              
              <input
                type="file"
                name="myImage"
                onChange={(event) => {
                  console.log(event.target.files[0]);
                  setSelectedImage(event.target.files[0]);
                }}
              />
               <br />
              <input type="text">Name</input> <br />
              <input type="text">Description</input> <br />
              <input type="text">Price</input> <br />
            </div>
             
            }
            <button>Add</button>
            <button onClick={() => setShowModal(false)}>Close</button>
          </div>
        </div>
      )}

      { <div>

      {
        showCart ?
          <CartList cart={cart} ></CartList> :
          <ProductList product={product} addToCart={addToCart} ></ProductList>
      }

      
    </div>}
    </div>
  );
}

export default App;
